export const alerts=e=>({list:()=>e.request("GET","/v1/alerts"),create:t=>e.request("POST","/v1/alerts",t),get:t=>e.request("GET",`/v1/alerts/${t}`),update:(t,r)=>e.request("PUT",`/v1/alerts/${t}`,r),delete:t=>e.request("DELETE",`/v1/alerts/${t}`),toggle:t=>e.request("PUT",`/v1/alerts/${t}/toggle`)});